<script lang="ts">
  import * as combobox from "@ibrains-design/combobox"
  import { useMachine, normalizeProps } from "@ibrains-design/svelte"

  const comboboxData = [
    { label: "Zambia", code: "ZA" },
    { label: "Benin", code: "BN" },
    //...
  ]

  let options = $state.raw(comboboxData)

  const collection = combobox.collection({
    items: comboboxData,
    itemToValue: (item) => item.code,
    itemToString: (item) => item.label,
  })

  const [snapshot, send] = useMachine(
    combobox.machine({
      id: "1",
      collection,
      onOpenChange() {
        options = comboboxData
      },
      onInputValueChange({ inputValue }) {
        const filtered = comboboxData.filter((item) => item.label.toLowerCase().includes(inputValue.toLowerCase()))
        const newOptions = filtered.length > 0 ? filtered : comboboxData

        collection.setItems(newOptions)
        options = newOptions
      },
    }),
  )

  const api = $derived(combobox.connect(snapshot, send, normalizeProps))
</script>

<div {...api.getRootProps()}>
  <label {...api.getLabelProps()}>Select country</label>
  <div {...api.getControlProps()}>
    <input {...api.getInputProps()} />
    <button {...api.getTriggerProps()}>â–¼</button>
  </div>
</div>
<div {...api.getPositionerProps()}>
  {#if options.length > 0}
    <ul {...api.getContentProps()}>
      {#each options as item}
        <li {...api.getItemProps({ item })}>
          {item.label}
        </li>
      {/each}
    </ul>
  {/if}
</div>
